<?php if ($_SERVER["REQUEST_METHOD"] == "POST") { $to = "armel12bedjra@gmail.com"; $subject = "Nouveau message du formulaire de contact"; $name = htmlspecialchars($_POST["name"]); $email = htmlspecialchars($_POST["email"]); $message = htmlspecialchars($_POST["message"]); $body = "Nom: $name\nEmail: $email\nMessage:\n$message"; $headers = "From: $email\r\n" . "Reply-To: $email\r\n" . "X-Mailer: PHP/" . phpversion(); if (mail($to, $subject, $body, $headers)) { echo "Message envoyé avec succès."; } else { echo "Erreur lors de l'envoi du message."; } } else { echo "Méthode non autorisée."; } ?>
